import React from 'react';
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';

const StripeCheckoutButton = ({price}) => {
    // stripe wants the payment process in cents
    const priceForStripe = price * 100;
    const publishableKey = "pk_test_51IIo79IXz3gRl48SXeanDl8BMOWus2uodd2HFSdkJTsNVhq2aospkmksBb82IBmxkikyqWIDCTRLXqXa53sVL1wX00EgrtPsPk";

    //passed the token to the backend, the stripe server
    //axios is used for passing data to the backend
    const onToken = token =>{
        axios({
            url:'payment',
            method:'post',
            data:{
                amount:priceForStripe,
                token
            }
        })
        .then(response =>{
            alert('Payment Successful');
        })
        .catch(error =>{
            console.log('Payment error: ', error);
            alert('There was an issue with your payment. Please make use you use the provided credit card. ')
        })
    }

    //components from the git stripe page 
    return (
        <StripeCheckout
            label='Pay Now'
            name='JG Clothing'
            billingAddress
            shippingAddress
            image='https://svgshare.com/i/CUz.svg'
            description={`Your total is $${price}`}
            amount={priceForStripe}
            panelLabel='Pay Now'
            token={onToken}
            stripeKey={publishableKey}
        />
    )
}

export default StripeCheckoutButton;
